import 'dart:typed_data';

import 'package:flutter/material.dart';
import 'package:flutter_svg/svg.dart';
import 'package:image_picker/image_picker.dart';
import 'package:instagram_clone/core/app_responsive_layout.dart';
import 'package:instagram_clone/services/authentication_service.dart';
import 'package:instagram_clone/utils/functions.dart';

import '../../core/app_colors.dart';
import '../../core/app_images.dart';
import '../../core/app_mobile_layout.dart';
import '../../core/app_web_layout.dart';
import '../../widgets/custom_button.dart';
import '../../widgets/custom_text_field.dart';

class SignupPage extends StatefulWidget {
  const SignupPage({Key? key}) : super(key: key);

  @override
  State<SignupPage> createState() => _SignupPageState();
}

class _SignupPageState extends State<SignupPage> {
  final TextEditingController _emailController = TextEditingController();
  final TextEditingController _passwordController = TextEditingController();
  final TextEditingController _biographyController = TextEditingController();
  final TextEditingController _usernameController = TextEditingController();
  Uint8List? image;
  bool isLoading = false;

  @override
  void dispose() {
    super.dispose();
    _emailController.dispose();
    _passwordController.dispose();
    _biographyController.dispose();
    _usernameController.dispose();
  }

  void selectImageProfile() async {
    Uint8List _imageSelected = await pickImage(ImageSource.gallery);

    setState(() {
      image = _imageSelected;
    });
  }

  void changedLoagind() {
    setState(() {
      isLoading = !isLoading;
    });
  }

  void signUpUser() async {
    changedLoagind();
    String result = await AutheticationService().signUpUser(
        username: _usernameController.text,
        email: _emailController.text,
        password: _passwordController.text,
        biography: _biographyController.text,
        photoFile: image!);

    changedLoagind();
    if (result != "Success") {
      showSnackBar(result, context);
    } else {
      Navigator.of(context).pushReplacement(MaterialPageRoute(
        builder: (context) => const AppResponsiveLayout(
            mobileLayout: AppMobileLayout(), webLayout: AppWebLayout()),
      ));
    }
  }

  static const String defaultImage =
      'data:image/png;base64,';

  Widget get imageSelected => image != null
      ? CircleAvatar(
          radius: 64,
          backgroundImage: MemoryImage(image!),
        )
      : const CircleAvatar(
          radius: 64,
          backgroundImage: NetworkImage(defaultImage),
        );

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SafeArea(
        child: Container(
          width: double.infinity,
          padding: const EdgeInsets.symmetric(horizontal: 32),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.center,
            children: [
              Flexible(child: Container(), flex: 1),
              SvgPicture.asset(
                imageLogo,
                color: primaryColor,
                height: 64,
              ),
              const SizedBox(height: 64),
              Stack(
                children: [
                  imageSelected,
                  Positioned(
                    bottom: -10,
                    left: 80,
                    child: IconButton(
                      onPressed: () => selectImageProfile(),
                      icon: const Icon(Icons.add_a_photo),
                    ),
                  ),
                ],
              ),
              const SizedBox(height: 24),
              CustomTextField(
                  textEditingController: _usernameController,
                  hintText: 'Username',
                  textInputType: TextInputType.text),
              const SizedBox(height: 24),
              CustomTextField(
                  textEditingController: _emailController,
                  hintText: 'Email',
                  textInputType: TextInputType.emailAddress),
              const SizedBox(height: 24),
              CustomTextField(
                textEditingController: _passwordController,
                hintText: 'Password',
                textInputType: TextInputType.text,
                isPassword: true,
              ),
              const SizedBox(height: 24),
              CustomTextField(
                textEditingController: _biographyController,
                hintText: 'Biography',
                textInputType: TextInputType.text,
              ),
              const SizedBox(height: 24),
              CustomButton(
                textButton: 'Sing up',
                onTap: () {
                  signUpUser();
                },
                isLoading: isLoading,
              ),
              const SizedBox(height: 12),
              Flexible(child: Container(), flex: 2),
              Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Container(
                    child: const Text(
                      "Don't have an account? ",
                    ),
                    padding: const EdgeInsets.symmetric(vertical: 8),
                  ),
                  GestureDetector(
                    onTap: () {},
                    child: Container(
                      child: const Text(
                        "Sign up",
                        style: TextStyle(fontWeight: FontWeight.bold),
                      ),
                      padding: const EdgeInsets.symmetric(vertical: 8),
                    ),
                  ),
                ],
              )
            ],
          ),
        ),
      ),
    );
  }
}
